import * as firebase from "firebase";
import axios from "axios";
import Geolocation from "@react-native-community/geolocation";
const synonyms = require("synonyms");

class Items {
  constructor() {
    // Your web app's Firebase configuration
    var firebaseConfig = {
      apiKey: "AIzaSyDZPaKcPaC0xnoxM1qYsUPEbuON1krqlP4",
      authDomain: "seek-525ee.firebaseapp.com",
      databaseURL: "https://seek-525ee.firebaseio.com",
      projectId: "seek-525ee",
      storageBucket: "seek-525ee.appspot.com",
      messagingSenderId: "803806872501",
      appId: "1:803806872501:web:ce8936ff30475b2bed4ffc",
      measurementId: "G-2FWD6N6E9V"
    };
    if (!firebase.apps.length) {
      // Initialize Firebase
      firebase.initializeApp(firebaseConfig);
      firebase.analytics();
    }
    // fire store
    this.db = firebase.firestore();
  }

  async loadAllMatch(name) {
    const snapshot = await this.db.collection("items").get();
    console.log(synonyms("good"));
    Geolocation.getCurrentPosition((info) => {
      axios
        .get(
          `https://maps.googleapis.com/maps/api/geocode/json?latlng=${info.coords.latitude},${info.coords.longitude}&key=AIzaSyDUAgs6Oz0iTMoHUzxuv95F4yN4ehY2xrc`
        )
        .then((result) => {
          let words = result.data.results[0].address_components;
          let postcode = words[words.length - 1].long_name;
          console.log(postcode);
        });
    });

    return snapshot.docs.map((doc) => {
      return { itemId: doc.id, data: doc.data() };
    });
  }
}

export { Items };
